Here's the completed class for reference:
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs/Observable';
import { Poll } from './poll';
import { HttpClient } from '@angular/common/http';

@Injectable()
export class PollService {
  private readonly baseUrl = 'https://8xe450t0j5.execute-api.us-west-2.amazonaws.com/Prod';

  constructor(private http: HttpClient) {}

  getPolls(): Observable> {
    return this.http.get(this.baseUrl + '/polls') as Observable;
  }

  createPoll(poll: Poll): Observable {
    return this.http.post(this.baseUrl + '/polls', poll) as Observable;
  }

  castVote(poll: Poll, answerIndex: number): Observable {
    return this.http.patch(`${this.baseUrl}/polls/${poll.id}/answer/${answerIndex}`, {});
  }
}

